def counter = 0;

List<String> wordsToCheck = new ArrayList<String>();
List<String> responseCorrected = new ArrayList<String>();

wordsToCheck.add("farest")
wordsToCheck.add("grean")
wordsToCheck.add("dest")

responseCorrected.add("forest")
responseCorrected.add("green")
responseCorrected.add("dust")

for ( counter; counter <  wordsToCheck.size; counter++) {
        	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("wrongWordProperty", wordsToCheck.getAt(counter) )
          testRunner.testCase.getTestStepByName("Properties").setPropertyValue("responseCorrected", responseCorrected.getAt(counter) )
        	testRunner.runTestStepByName("checkWord");
}

